mapper.select:
SELECT COUNT(*) cnt FROM outsourcing
AND outsourcing_name LIKE CONCAT('%',#{searchWord},'%')
AND outsourcing_type = #{searchType}

mapper.update:
UPDATE 
	outsourcing_rsvn 
SET 
	rsvn_title = #{rsvnTitle}

		, rsvn_state = #{rsvnState}

	, rsvn_content = #{rsvnContent}
	, rsvn_start = #{rsvnStart}
	, rsvn_end = #{rsvnEnd}
	, mod_id = #{modId}
	, mod_time = NOW()
WHERE 
	rsvn_no = #{rsvnNo}

mapper.insert:


SELECT LAST_INSERT_ID()


INSERT INTO outsourcing(
	outsourcing_no
	,outsourcing_pw
	,outsourcing_name
	,outsourcing_state
	,outsourcing_type
	,incharge_name
	,contract_start
	,contract_end
	,emp_no
	,contact_no
	,post_no
	,address
	,reg_id
	,reg_time
	,mod_id
	,mod_time
	,use_yn
)VALUES(
	#{outsourcingNo}
	,#{outsourcingPw}
	,#{outsourcingName}
	,'3'
	,#{outsourcingType}
	,#{inchargeName}
	,#{contractStart}
	,#{contractEnd}
	,#{empNo}
	,#{contactNo}
	,#{postNo}
	,#{address}
	,#{regId}
	,NOW()
	,#{modId}
	,NOW()
	,'Y'
);

INSERT INTO file_upload(
	file_no, reference_no, original_file_name
	, file_name, file_size, file_type
	, reg_time, mod_time, reg_id, mod_id, use_yn
)VALUES(
	CURRENT_TIMESTAMP(5), #{referenceNo}, #{OriginalFileName}
	, #{fileName}, #{fileSize}, #{fileType}
	, NOW(), NOW(), #{regId}, #{modId}, #{useYN}
)
