mapper.select:
SELECT g.gst_id gstId, g.gst_name gstName, r.room_dong roomDong, r.room_ho roomHo FROM guest g INNER JOIN room r ON g.room_no = r.room_no WHERE g.use_yn = 'Y' AND g.gst_type = '입주자'
AND (g.gst_name LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{beginRow}, #{rowPerPage}

SELECT g.gst_id gstId, g.gst_name gstName, r.room_dong dong, r.room_type type, r.room_ho ho FROM guest g INNER JOIN room r ON g.room_no = r.room_no WHERE g.room_no IS NOT NULL AND g.use_yn = 'Y' AND g.gst_type = '입주자'
AND r.room_dong = #{dong}
AND r.room_type = #{type}
AND r.room_ho = #{ho}
LIMIT #{beginRow}, #{rowPerPage}
